pragma solidity ^0.8.10;

import "./JToken.sol";

abstract contract PriceOracle {
  /// @notice Indicator that this is a PriceOracle contract (for inspection)
  bool public constant isPriceOracle = true;

  /**
   * @notice Get the underlying price of a jToken asset
   * @param jToken The jToken to get the underlying price of
   * @return The underlying asset price mantissa (scaled by 1e18).
   *  Zero means the price is unavailable.
   */
  function getUnderlyingPrice(JToken jToken) external view virtual returns (uint);

  /**
   * @notice Get the price of a specific asset
   * @param asset The asset to get the price of
   * @return The asset price mantissa (scaled by 1e18).
   *  Zero means the price is unavailable.
   */
  function getAssetPrice(address asset) external view virtual returns (uint);
}
","keccak256":"0x01b3177ee4b79bae046885ced531584dce7e1f95945048fa8e36fa6699ec30bc","license":"BSD-3-Clause"},"contracts/SimplePriceOracle.sol":{"content":"// SPDX-License-Identifier: BSD-3-Clause
pragma solidity ^0.8.10;

import "./PriceOracle.sol";
import "./JErc20.sol";

contract SimplePriceOracle is PriceOracle {
  mapping(address => uint) prices;
  event PricePosted(
    address asset,
    uint previousPriceMantissa,
    uint requestedPriceMantissa,
    uint newPriceMantissa
  );

  function _getUnderlyingAddress(JToken jToken) private view returns (address) {
    address asset;
    if (compareStrings(jToken.symbol(), "jJBC")) {
      asset = 0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE;
    } else {
      asset = address(JErc20(address(jToken)).underlying());
    }
    return asset;
  }

  function getUnderlyingPrice(JToken jToken) public view override returns (uint) {
    return prices[_getUnderlyingAddress(jToken)];
  }

  // v1 price oracle interface for use as backing of proxy
  function getAssetPrice(address asset) public view override returns (uint) {
    return prices[asset];
  }

  function setUnderlyingPrice(JToken jToken, uint underlyingPriceMantissa) public {
    address asset = _getUnderlyingAddress(jToken);
    emit PricePosted(asset, prices[asset], underlyingPriceMantissa, underlyingPriceMantissa);
    prices[asset] = underlyingPriceMantissa;
  }

  function setDirectPrice(address asset, uint price) public {
    emit PricePosted(asset, prices[asset], price, price);
    prices[asset] = price;
  }

  function compareStrings(string memory a, string memory b) internal pure returns (bool) {
    return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
  }
}
